.block-domain .input-group {
  box-shadow: 0 0 30px 20px #e6ebee;
  border: 0;
  margin: 4rem 0;
}


.block-domain__prices{
  color: var(--color-heading);
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 6rem) ;
  font-size: 2rem;
  font-weight: 600;
  justify-items: center;
  max-width: 700px;
  margin: 4rem auto;
  max-width: 670px;
}


@media screen and (min-width: 768px){
  .block-domain__prices{
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
}


/* plans block  */

.block-plans .grid{
  gap: 8rem 4rem;
}

.block-plans .card{
  max-width: 500px;
  margin: 0 auto;
}

/* Features */

.feature{
  gap: 4rem 2rem;
  margin: 12rem 0;
}
.feature__heading{
  margin: 1rem 0;
}

.feature__image{
  width: 100%;
}
.feature:first-of-type{
  margin-top: 6rem;
}
@media screen and (min-width: 768px) {
  .feature:nth-of-type(even) .feature__content {
    order: 2;
  }
}


/* Showcase Block  */

.feature {
  gap: 4rem 2rem;
  margin: 12rem 0;
}

.feature:first-of-type {
  margin-top: 6rem;
}

.feature__heading {
  margin: 1rem 0;
}

.feature__image {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .feature:nth-of-type(even) .feature__content {
    order: 2;
  }
}

/* Showcase Block */
.block-showcase__image > img {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .block-showcase .grid {
    grid-template-columns: 50% 50%;
  }

  .block-showcase__image {
    justify-self: end;
  }
  .block-showcase__image  > img{
    justify-self: end;
    width: auto;
    max-width: 700px;
  }


}


/* footer */

.footer{
  background: #232323;
}
.footer a {
  color: #7777;
  transition: color .3s;

}
footer li{
  font-size: 1.6rem;
}
.footer a:hover{
  color: #fff;
}
.footer__section{
  padding: 2rem;
  border-bottom: 1px solid #393939;
}

.footer__heading{
  text-transform: uppercase;
  font-weight: 600;
}

.footer__brand{
  text-align: center;
  margin-top: 5rem;
}
.footer__copyright{
  font-size: 2.1rem;
  color: #fff;
  opacity: .3;
}
.footer__section .list{
  margin: 0;
}
.footer__brand img{
  width: 100%;
  max-width: 230px;
}
@media screen and (min-width: 768px) {
  .footer__sections{
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
  .footer .collapsible__chevron{
    display: none;
  }
  .footer .collapsible__content{
    opacity: 1;
    max-height: 100%;
  }
  .footer__brand{
    order: -1;
    margin-top: 1rem;
  }
  .footer__copyright{
    font-size: 1.5rem;
  }
  .footer__section{
    border: 0;
  }
  .footer__heading{
    font-size: 1.6rem;
  }

}

.callout-footer{
  transform: translateY(5rem);
}

