@media screen and (min-width: 1024px) {
  .input {
    font-size: 1.5rem;
  }
}

/* Card */

.card {
  border-radius: 7px;
  box-shadow: 0 0 20px 21px #f3f3f3;
  overflow: hidden;
}

.card__header,
.card__body {
  padding: 2rem 3rem;
}

.card--primary .card__header {
  background-color: var(--color-primary);
  color: #fff;
}
.card--secondary .card__header {
  background-color: var(--color-secondary);
  color: #fff;
}

/* plan */
.plan{
  transition: transform .2s ease-out;
}

.plan__name {
  color: #fff;
  margin: 0;
  font-weight: 500;
  font-size: 2.4rem;
}
.plan__price {
  font-size: 6rem;
}
.plan__billing-sycle {
  opacity: 0.8;
  font-size: 2.4rem;
  font-weight: 300;
  margin-right: 1rem;
}
.card--secondary .badge--secondary {
  background: #02cdf1;
}
.plan__description {
  font-size: 2rem;
  font-weight: 300;
  letter-spacing: 1px;
  display: block;
}

.plan--popular .card__header {
  position: relative;
}
.plan--popular .card__header::before {
  content: url(../img/popular.svg);
  display: inline-block;
  position: absolute;
  right: 5%;
  top: -6px;
  width: 40px;
}

.plan .list__item {
  margin-bottom: 2rem;
}

.plan:hover{
  transform: scale(1.05);
}

.plan--popular:hover{
  transform: scale(1.15);
}

@media screen and (min-width:1024px) {

  .plan__name {
    font-size: 1.4rem;
  }
  .plan__price {
    font-size: 5rem;
  }
  .plan__billing-sycle {
    font-size: 1.6rem;
  }
  .plan__description {
    font-size: 1.7rem;
  }
  .plan--popular{
  transform: scale(1.1);
  }
}

/* Media */

.media {
  display: flex;
  margin-bottom: 4rem;
}
.media__title {
  margin-top: 0;
}
.media__body {
  margin: 0 2rem;
}
.media__image {
  margin-top: 1rem;
}

/* Quote */

.quote {
  font-size: 3rem;
  font-style: italic;
  color: var(--color-body-darker);
  /* line-height: 1.3; */
}

.quote__text::before {
  content: open-quote;
}

.quote__text::after {
  content: close-quote;
}

.quote__author {
  font-size: 3rem;
  font-weight: 500;
  font-style: normal;
  margin-bottom: 0;
}

.quote__organization {
  color: var(--color-heading);
  opacity: 0.4;
  font-size: 2rem;
  font-style: normal;
}

.quote__line {
  position: relative;
  bottom: 10px;
}

@media screen and (min-width: 1024px) {
  .quote {
    font-size: 2rem;
  }
  .quote__author {
    font-size: 2.4rem;
  }
  .quote__organization {
    font-size: 1.6rem;
  }
}

/* Grid */
.grid {
  display: grid;
}
@media screen and (min-width: 768px) {
  .grid--1x2 {
    grid-template-columns: repeat(2, 1fr);
  }
}
@media screen and (min-width: 1024px) {
  .grid--1x3 {
    grid-template-columns: repeat(3, 1fr);
  }
}

/* Testimonial */
.testimonial {
  padding: 3rem;
}
.testimonial__image > img {
  width: 100%;
}

.testimonial__image {
  position: relative;
}

.testimonial__image > .icon-container {
  position: absolute;
  top: 3rem;
  right: -32px;
}

@media screen and (min-width: 768px) {
  .testimonial .quote,
  .testimonial .quote__author {
    font-size: 2.4rem;
  }
  .testimonial .quote {
    margin-left: 6rem;
  }
}

/* Callout */
.callout {
  padding: 4rem;
  border-radius: 5px;
}
.callout--primary {
  background: var(--color-primary);
  color: #fff;
}

.callout--heading {
  margin-top: 0;
  color: #fff;
  font-size: 3rem;
}
.callout .btn {
  justify-self: center;
  align-self: center;
}

.callout--content {
  text-align: center;
}

@media screen and (min-width: 768px) {
  .callout .grid--1x2 {
    grid-template-columns: 1fr auto;
  }
  .callout--content {
    text-align: left;
  }
  .callout .btn {
    justify-self: start;
    margin: 0 2rem;
  }
}

/* Blocks */

.block {
  --padding-vertical: 6rem;
  padding: var(--padding-vertical) 2rem;
  /* text-align: center; */
}
.block__header {
  text-align: center;
  margin-bottom: 4rem;
}
.block__heading {
  margin-top: 0;
}
.block--dark {
  background: #000;
  color: #7b858b;
}
.block--dark h1, 
.block--dark h2,
.block--dark h3 {
  color: #fff;
}
.block--skewed-right {
  padding-bottom: calc(var(--padding-vertical) + 4rem);
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 90%);
}
.block--skewed-left {
  padding-bottom: calc(var(--padding-vertical) + 4rem);
  clip-path: polygon(0% 0%, 100% 0%, 100% 90%, 0% 100%);
}

.container {
  max-width: 1140px;
  margin: 0 auto;
}

/* collapsible */

.collapsible__header {
  display: flex;
  justify-content: space-between;
}
.collapsible__heading {
  margin-top: 0;
  font-size: 3rem;
}
.collapsible__chevron {
  transform: rotate(-90deg);
  transition: transform 0.3s;
}
.collapsible__content {
  max-height: 0;
  overflow: hidden;
  transition: all 0.3s;
  opacity: 0;
}
.collapsible--expanded .collapsible__chevron {
  transform: rotate(0);
}
.collapsible--expanded .collapsible__content {
  opacity: 1;
  max-height: 100vh;
}

/* Navbar */

.nav {
  display: flex;
  background: #000;
  justify-content: space-between;
  flex-wrap: wrap;
  padding: 0 1rem;
  align-items: center;
}

.nav__list {
  width: 100%;
  margin: 0;
}
.nav__item {
  padding: 0.5rem 2rem;
  border-bottom: 1px solid #222;
}
.nav__item > a {
  color: #d2d0db;
  transition: color 0.3s;
}
.nav__item > a:hover {
  color: #fff;
}

.nav__toggler {
  opacity: 0.5;
  transition: box-shadow 0.15s;
  cursor: pointer;
}

.nav.collapsible--expanded .nav__toggler {
  opacity: 1;
  box-shadow: 0 0 0 3px #666;
  border-radius: 5px;
}
.nav__brand {
  transform: translateY(5px);
}
@media screen and (min-width: 768px) {
  .nav__toggler {
    display: none;
  }
  .nav__list {
    display: flex;
    width: auto;
    font-size: 1.6rem;
    opacity: 1;
    max-height: 100%;
  }
  .nav__item {
    border: 0;
  }
}
/* Hero */

.hero__tagline {
  font-size: 2rem;
  color: #b9c3cf;
  letter-spacing: 1px;
  margin: 2rem 0 5rem;
}
.hero__image {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .hero {
    padding-top: 0;
  }
  .hero__content {
    text-align: left;
    align-self: center;
  }
}

/* Block domain */

.block-domain .input-group {
  box-shadow: 0 0 30px 20px #e6ebee;
  border: 0;
  margin: 4rem 0;
}


.block-domain__prices{
  color: var(--color-heading);
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 6rem) ;
  font-size: 2rem;
  font-weight: 600;
  justify-items: center;
  max-width: 700px;
  margin: 4rem auto;
  max-width: 670px;
}


@media screen and (min-width: 768px){
  .block-domain__prices{
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
}


/* plans block  */

.block-plans .grid{
  gap: 8rem 4rem;
}

.block-plans .card{
  max-width: 500px;
  margin: 0 auto;
}

/* Features */

.feature{
  gap: 4rem 2rem;
  margin: 12rem 0;
}
.feature__heading{
  margin: 1rem 0;
}

.feature__image{
  width: 100%;
}
.feature:first-of-type{
  margin-top: 6rem;
}
@media screen and (min-width: 768px) {
  .feature:nth-of-type(even) .feature__content {
    order: 2;
  }
}


/* Showcase Block  */

.feature {
  gap: 4rem 2rem;
  margin: 12rem 0;
}

.feature:first-of-type {
  margin-top: 6rem;
}

.feature__heading {
  margin: 1rem 0;
}

.feature__image {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .feature:nth-of-type(even) .feature__content {
    order: 2;
  }
}

/* Showcase Block */
.block-showcase__image > img {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .block-showcase .grid {
    grid-template-columns: 50% 50%;
  }

  .block-showcase__image {
    justify-self: end;
  }
  .block-showcase__image  > img{
    justify-self: end;
    width: auto;
    max-width: 700px;
  }


}


/* footer */

.footer{
  background: #232323;
}
.footer a {
  color: #7777;
  transition: color .3s;

}
footer li{
  font-size: 1.6rem;
}
.footer a:hover{
  color: #fff;
}
.footer__section{
  padding: 2rem;
  border-bottom: 1px solid #393939;
}

.footer__heading{
  text-transform: uppercase;
  font-weight: 600;
}

.footer__brand{
  text-align: center;
  margin-top: 5rem;
}
.footer__copyright{
  font-size: 2.1rem;
  color: #fff;
  opacity: .3;
}
.footer__section .list{
  margin: 0;
}
.footer__brand img{
  width: 100%;
  max-width: 230px;
}
@media screen and (min-width: 768px) {
  .footer__sections{
    grid-template-columns: repeat(auto-fit, minmax(10rem, 1fr));
  }
  .footer .collapsible__chevron{
    display: none;
  }
  .footer .collapsible__content{
    opacity: 1;
    max-height: 100%;
  }
  .footer__brand{
    order: -1;
    margin-top: 1rem;
  }
  .footer__copyright{
    font-size: 1.5rem;
  }
  .footer__section{
    border: 0;
  }
  .footer__heading{
    font-size: 1.6rem;
  }

}

.callout-footer{
  transform: translateY(5rem);
}
